from itertools import permutations


def checkDir(grid, row, col, rowDiff, colDiff):
    s = ""
    while (
        0 <= row and row < len(grid) and 0 <= col and col < len(grid[0]) and len(s) < 4
    ):
        s += grid[row][col]
        row += rowDiff
        col += colDiff
    return s == "XMAS"


def checkAll(grid, row, col):
    ans = 0
    dirs = set(permutations([1, 1, 0, -1, -1], 2))
    for (rowDiff, colDiff) in dirs:
        ans += checkDir(grid, row, col, rowDiff, colDiff)
    return ans


def solution1(inp):
    grid = inp.split("\n")
    letters = set(["X", "M", "A", "S"])
    ans = 0
    for row in range(len(grid)):
        for col in range(len(grid[1])):
            if grid[row][col] in letters:
                ans += checkAll(grid, row, col)
    return ans


def solution2(inp):
    grid = inp.split("\n")
    ans = 0
    letters = set(["M", "S"])
    for row in range(1, len(grid) - 1):
        for col in range(1, len(grid[1]) - 1):
            if grid[row][col] == "A":
                topleft = grid[row - 1][col - 1]
                bottomright = grid[row + 1][col + 1]
                if set([topleft, bottomright]) == letters:
                    topright = grid[row - 1][col + 1]
                    bottomleft = grid[row + 1][col - 1]
                    if set([topright, bottomleft]) == letters:
                        ans += 1
    return ans


